"use strict";function submitHandler(n){n.preventDefault(),getDataFromAPI(document.querySelector("#form-input").value)}function getDataFromAPI(n){var a='http://query.yahooapis.com/v1/public/yql?q=select * from weather.forecast where woeid in (select woeid from geo.places(1) where text="'+n+'") and u=%22c%22&format=json';axios(a).then(function(n){return n.data.query.results.channel}).then(function(n){console.log(n),displayWeatherData(n)})}function displayWeatherData(n){var a=n.location,s=n.item,e=n.units,t=n.wind,r=n.atmosphere,p=document.createElement("div");p.setAttribute("class","search__output"),p.innerHTML='\n    <div class="search__output__main">\n      <h6 class="output__city">'+a.city+", "+a.region+", "+a.country+'</h6>\n      <h1 class="output__temperature">'+s.condition.temp+"°"+e.temperature+" "+s.condition.text+'</h1>\n\n      <div class="output__temperature-sub">\n        <span><img src="./images/down-arrow.png" width="16" alt="Down Arrow">16°</span>\n        <span><img src="./images/up-arrow.png" width="16" alt="Down Arrow">25°</span>\n        <span style="padding-left: .9rem;"><span class="fw-300">Sensação</span> 19°C</span>\n      </div>\n\n      <div class="output__wind-humidity">\n        <span>Vento <span class="fw-800">'+t.speed+e.speed+'</span></span>\n        <span style="padding-left: .9rem;">Humidade <span class="fw-800">'+r.humidity+'%</span></span>\n      </div>\n\n    </div> \n\n    <hr class="hr__custom--small-orange">\n\n    <div class="search__output__footer">\n      <ul class="clearfix">\n        <li>\n          <span class="day">Terça</span> <span class="temperature">18° 26°</span>\n        </li>\n\n        <li>\n          <span class="day">Quarta</span> <span class="temperature">18° 26°</span>\n        </li>\n\n        <li>\n          <span class="day">Quinta</span> <span class="temperature">18° 26°</span>\n        </li>\n\n        <li>\n          <span class="day">Sexta</span> <span class="temperature">18° 26°</span>\n        </li>\n\n        <li>\n          <span class="day">Sábado</span> <span class="temperature">18° 26°</span>\n        </li>\n      </ul>\n\n    </div>\n  ';var i=document.querySelector("#form");document.querySelector("#search").insertBefore(p,i)}